#include &lt;avr/interrupt.h&gt;
#define INTERRUPT_PIN PCINT3  // This is PB3 per the schematic
#define PCINT_VECTOR PCINT0_vect      // This step is not necessary - it&#039;s a naming thing for clarit
#define TOUCH_IN 3 // PB3
bool touched = false;

void setup() {
    // setup touch input pin as pin change interrupt pin
    cli();                            // Disable interrupts during setup
    PCMSK |= (1 &lt;&lt; INTERRUPT_PIN);    // Enable interrupt handler (ISR) for our chosen interrupt pin (PCINT3/PB3/pin #3)
    GIMSK |= (1 &lt;&lt; PCIE);             // Enable PCINT interrupt in the general interrupt mask
    pinMode(TOUCH_IN, INPUT);
    sei();                            //last line of setup - enable interrupts after setup
}

