digraph workflow{
    rankdir=TB; bgcolor=&quot;transparent&quot;;
    graph [nodesep=&quot;0.5&quot;, ranksep=&quot;0.2&quot;]
    node [shape=box]

    subgraph cluster_kn{
        label = &lt;&lt;font color=&quot;red&quot;&gt;loop through &lt;/font&gt; all &amp;lt;x,w&amp;gt; pairs&gt;
        node [shape = box];
        V_N [label=&quot;V(N,x,w)←J(x,w)&quot;];
    }

    subgraph cluster_kother{
        label = &lt;&lt;font color=&quot;red&quot;&gt;loop through &lt;/font&gt; all &amp;lt;x,w&amp;gt; pairs&gt;
        node [shape = box];
        Vpi_init [label=&quot;V(&lt;x,w&gt;)←+inf\n pi(&lt;x,w&gt;)←0&quot;];
        Vpi [label=&quot;V(&lt;x,y&gt;)←min(V(&lt;x,y&gt;), g+E)\n pi(&lt;x,w&gt;)←argmin(V(&lt;x,y&gt;), g+E)&quot;];

        subgraph cluster_u{
            label = &lt;&lt;font color=&quot;red&quot;&gt;loop through &lt;/font&gt; all u&gt;
            node [shape = box]
            g [label=&quot;g←g(x,w,u)&quot;];
            E_init [label=&quot;E←0&quot;];

            subgraph cluster_min{
                label = &lt;&lt;font color=&quot;red&quot;&gt;loop through &lt;/font&gt; all subsequent &amp;lt;x&#039;,w&#039;&amp;gt; pairs&gt;
                node [shape = box]
                E_sum [label=&quot;E(x,w,u)=sum(P*V):\n E ← E + P(&lt;x&#039;,w&#039;&gt;|&lt;x,w&gt;,u)V(&lt;x&#039;,w&#039;&gt;)&quot;]
            }
        }
    }

    node [shape = box];
    init[label=&quot;Init all V(s)←0&quot;];

    node [shape = diamond];
    k_N [label=&quot;k=N&quot;];


    node [shape = box, style=&quot;rounded&quot;];
    start[label=&quot;start: backward induction&quot;];
    end[label=&quot;end&quot;];

    start -&gt; init;
    init -&gt; k_N;
    k_N:w -&gt; V_N:n [label=&quot;Y&quot;];
    V_N:w -&gt; V_N:w;

    k_N:e -&gt; Vpi_init:n [label=&quot;N&quot;];
    Vpi_init -&gt; g;
    g -&gt; E_init;
    E_init -&gt; E_sum;
    E_sum:e -&gt; E_sum:e;
    E_sum -&gt; Vpi;
    E_sum:e -&gt; g:e [constraint=false];
    Vpi:e -&gt; g:e [constraint=false];

    V_N:s -&gt; end:n;
    Vpi:s -&gt; end:n;
}

